import localFont from 'next/font/local'
 
// Font files can be colocated inside of `app`
const PPNeue = localFont({
  src: [
    {
      path: '../public/fonts/PPNeueMontreal/web/PPNeueMontreal-Regular.woff2',
      weight: '400',
      style: 'normal',
    },
    {
      path: '../public/fonts/PPNeueMontreal/web/PPNeueMontreal-SemiBold.woff2',
      weight: '700',
      style: 'normal',
    }
  ],
})

import { CacheProvider } from "@chakra-ui/next-js";
// import { ChakraProvider,  } from "@chakra-ui/react";

import { ChakraProvider, ColorModeScript } from '@chakra-ui/react'
import {
  extendTheme,
  withDefaultColorScheme,
  theme as baseTheme,
} from '@chakra-ui/react'

const TEXT_COLOR = '#4A4A4A'
const BORDER = '#B3B3B3'
const BUTTON_COLOR = '#FFDD00'

const customTheme = extendTheme(
  {
    initialColorMode: 'light',
    useSystemColorMode: false,
    styles: {
      global: {
        // styles for the `body`
        body: {
          // bg: 'gray.400',
          // color: 'white',
        },
        button: {
          color: BUTTON_COLOR,
        },
      },
    },
    fonts: {
      body: PPNeue.style.fontFamily,
      heading: PPNeue.style.fontFamily,
      mono: PPNeue.style.fontFamily,
    },
    colors: {
      brand: {
        100: '#FFED7A',
        200: '#FFEA65',
        300: '#FFE751',
        400: '#FFE53D',
        500: '#FFDD00',
        600: '#DABD00',
        700: '#B69D00',
        800: '#917E00',
        900: '#6D5E00',
      },
    },
    components: {
      Button: {
        baseStyle: {
          color: BUTTON_COLOR,
          fontWeight: 'semibold', // Normally, it is "semibold"
        },
        variants: {
          outline: {
            borderColor: BORDER,
            color: TEXT_COLOR
          },
          solid: {
            bg: 'brand.500',
            color: TEXT_COLOR
          },
          ghost: {
            color: TEXT_COLOR
          }
        },
      },
    },
  },
  withDefaultColorScheme({ colorScheme: 'brand' }),
)

export default function RootLayout({
  children,
}: Readonly<{
  children: React.ReactNode;
}>) {
  return (
    <html lang='en'>
      <body>
        <ColorModeScript initialColorMode="light" />
        <ChakraProvider 
          theme={customTheme}>
          {children}
        </ChakraProvider>
      </body>
    </html>
  );
}
